const App = () => {
  const categories = [
    {
      id: 1,
      title: "Hats",
      imageUrl: `${process.env.PUBLIC_URL}/images/bat-logo.png`,
    },
    {
      id: 2,
      title: "Jackets",
      imageUrl: `${process.env.PUBLIC_URL}/images/Gnh-skull.png`,
    },
    {
      id: 3,
      title: "Sneakers",
      imageUrl: `${process.env.PUBLIC_URL}/images/Jordancry.png`,
    },
    {
      id: 4,
      title: "Mens",
      imageUrl: `${process.env.PUBLIC_URL}/images/Knicks.png`,
    },
    {
      id: 5,
      title: "Womens",
      imageUrl: `${process.env.PUBLIC_URL}/images/Sesame-street.png`,
    },
  ];

  return (
    <div
      id="categories-container"
      className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 mt-12"
    >
      {categories.map(({ title, id, imageUrl }) => (
        <div
          id="category-container"
          className="min-w-32 h-auto flex flex-col items-center justify-center border border-black rounded-3xl shadow-lg mt-0 mx-1 mb-4 overflow-hidden hover:cursor-pointer"
          key={id}
        >
         <div className="flex flex-col w-full h-full">
         <div id="image" className="h-3/4">
            <img src={`${imageUrl}`} className="w-full h-full object-cover" />
          </div>
          <div
            id="category-label-container"
            className="w-full h-1/4 py-2 px-2 flex flex-row justify-center "
          >
            <h2
              id="category-name"
              className="font-bold text-22 text-gray-700"
            >
              {title}
            </h2>
            <p id="call-to-action" className="font-light text-16">
              Shop Now
            </p>
          </div>
         </div>
        </div>
      ))}
    </div>
  );
};

export default App;
